INSERT INTO `categories` (`id`, `name`, `slug`, `description`, `type`, `status`, `parent_id`, `created_at`, `updated_at`) VALUES
(1, 'Asia Watch', 'asia-watch', '<div>AsiaWATCH is an online magazine with stories, features, opinion and analysis from journalists around Southeast Asia primarily focused on geo-politics.</div>', 'main', 1, NULL, NULL, '2022-07-03 14:26:15'),
(3, 'Foreign Correspondent', 'foreign-correspondent', '<div>Far East Correspondent is a news portal directing readers to the websites, which our freelance correspondents write for. It provides a focal point for their work which primarily covers East Asia through an array of media including television, radio, text and photography.</div>', 'main', 1, NULL, NULL, '2022-07-03 13:08:14'),
(4, 'Highlights', 'highlights', NULL, 'main', 1, NULL, NULL, '2022-07-01 21:44:49'),
(5, 'Gallery', 'gallery', '<div>A collection of fine photographs form around the world available as prints.</div><div><br></div>', 'main', 1, NULL, NULL, '2022-07-03 14:26:41'),
(14, 'Pages', 'pages', '<div>This is used just to categorise pages</div>', 'pages', 1, 0, '2022-07-04 17:00:48', '2022-07-04 17:00:48'),
(15, 'Books', 'books', '<div>My Books and other selected authors.</div>', 'sub', 1, 0, '2022-07-05 06:38:56', '2022-07-05 06:38:56');

